#!/bin/bashset -eVERSIONS=("php73" "php82" "php83")echo "üöÄ Running async test for PHP versions: ${VERSIONS[*]}"echofor version in "${VERSIONS[@]}"; do  version_suffix=$(echo "$version" | sed 's/php//')  test_file="test-async-${version_suffix}.php"  test_path="$version/$test_file"  if [[ ! -f "$test_path" ]]; then    echo "‚ùå Test file '$test_path' not found. Skipping $version."    continue  fi  echo "üîß Building $version..."  docker-compose build "$version" > /dev/null  echo "üì¶ Installing dependencies for $version..."  docker-compose run --rm "$version" composer install > /dev/null  echo "‚ñ∂Ô∏è Running $test_file for $version..."  docker-compose run --rm "$version" php "$test_file"  echodoneecho "‚úÖ All versions completed."